import React, { useState, useEffect } from "react";

const HACKER_NEWS_API = `https://hacker-news.firebaseio.com/v0/item/`;

const Comment = ({ comment }) => {
  const [childComments, setChildComments] = useState([]);

  useEffect(() => {
    if (!comment.kids) {
      return;
    }

    Promise.all(comment.kids.map(fetchComment))
      .then((responses) => Promise.all(responses.map((r) => r.json())))
      .then((comments) => setChildComments(comments));
  }, [comment.kids]);

  return (
    <div style={{ marginLeft: "20px" }}>
      <p>{comment.text}</p>
      {childComments.map((c) => (
        <Comment key={c.id} comment={c} />
      ))}
    </div>
  );
};

const fetchComment = (id) =>
  fetch(`${HACKER_NEWS_API}${id}.json`).then((response) => response);

const CommentThread = ({ kids }) => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    Promise.all(kids.map(fetchComment))
      .then((responses) => Promise.all(responses.map((r) => r.json())))
      .then((comments) => setComments(comments));
  }, [kids]);

  return (
    <div>
      {comments.map((c) => (
        <Comment key={c.id} comment={c} />
      ))}
    </div>
  );
};

export default CommentThread;
